//router.js加载所有组件并且指定访问路径
import Vue from 'vue'
import Router from 'vue-router'
import Home from './views/Home.vue'
import About from './views/About.vue'
//1.1加载Home组件
import HomeContainer from "./components/tabbar/HomeContainer.vue"
//导入member文件
import member from './components/tabbar/member.vue'
import cart  from './components/tabbar/cart.vue'
import search from './components/tabbar/search.vue'
import news from './components/news/NewsList.vue'
import detail from './components/news/details.vue'
import comments from './components/subcomponents/comment.vue'
Vue.use(Router)
//导出路由
export default new Router({
  routes: [
    //如果访问/自动将请求路径改 /home 重定向
    {path:'/',redirect:"/home"},
    {
      //path组件访问路由   
      path: '/home',
      //对应组件
      //方法1如果访问/home  a  <router-link to="/home">
      component: HomeContainer
    },
    {path:'/member',component:member},
    {path:'/cart',component:cart},
    {path:'/search',component:search},
    {path:'/new',component:news},
    //传参<router-link to="'/detail/'+item.id" >
    //this.$route.params.id获取参数id的值
    {path:'/detail/:id',component:detail}
  ]
})
